import{_ as e,o as a,c,a as t}from"./app.e5b5e395.js";const d="/yueran-doc/assets/music.8ec8d0ee.png",f=JSON.parse('{"title":"关于守时","description":"","frontmatter":{},"headers":[{"level":2,"title":"故事环节","slug":"故事环节","link":"#故事环节","children":[]},{"level":2,"title":"情景分析","slug":"情景分析","link":"#情景分析","children":[]}],"relativePath":"inspiration/punctual.md","lastUpdated":1678027220000}'),o={name:"inspiration/punctual.md"},n=t('<h1 id="关于守时" tabindex="-1">关于守时 <a class="header-anchor" href="#关于守时" aria-hidden="true">#</a></h1><h2 id="故事环节" tabindex="-1">故事环节 <a class="header-anchor" href="#故事环节" aria-hidden="true">#</a></h2><p>在某互联网公司，有一位初入职场的年轻人叫<code>怕浪猫</code>，平时在公司和旁边几位同事都挺聊得来，在他入职的第四个星期，有位同事组织工位旁边的几位小伙伴周末爬山踏春，当然也询问了他，<code>对于单身的他来说，周末确实也挺空闲的，于是他答应了下来</code>。组织爬山活动的哪位同事，发布了具体的时间和出发地点。</p><p>本来约好<code>周六下午两点</code>，在<code>兰帕公园</code>集合的，<code>怕浪猫</code>本来计算好路程时间准备出发的，结果临时处理了一件事情，耽搁了半个小时，当快到两点时，发起活动的他位同事问他情况时，<code>怕浪猫</code>却谎称<code>快到了</code>，结果让大家等了他半个小时，到了后简单的一句<code>不好意思，临时遇到点事</code>, 然后就若无其事的样子了。 其实此时他的同事们，肯定是不爽的，对<code>怕浪猫</code>的印象也变得不好。</p><h2 id="情景分析" tabindex="-1">情景分析 <a class="header-anchor" href="#情景分析" aria-hidden="true">#</a></h2><p><img src="'+d+'" alt="守时"></p>',6),r=[n];function i(s,_,l,h,p,u){return a(),c("div",null,r)}const x=e(o,[["render",i]]);export{f as __pageData,x as default};
